// console.log('AAA--子进程被执行-->远程remoteScript被执行');
var url = process.argv[2];
var result = {};

var request = require('request');

request(url, function (error, response, body) {
  // console.log('AAA---->error:', error); // Print the error if one occurred
  // console.log('AAA---->statusCode:', response && response.statusCode); // Print the response status code if a response was received
  // console.log('AAA---->body:', body); // Print the HTML for the Google homepage.
  if (error) {
    result.success = false;
    result.content = '' + error;
  } else {
    result.success = true;
    result.content = body;
  }

  console.log(JSON.stringify(result));
});